export interface HttpResult<D> {
  code: number;
  data?: D;
}

export const createHttpError = (code: number, message: string): HttpResult<{ message: string }> => {
  return {
    code,
    data: { message },
  };
};

export const createHttpResponse = (code: number, data: object): HttpResult<object> => {
  return {
    code,
    data,
  };
};
